INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '09c9f3af4f4a4bcda8adea2d7c3da9c5.jpg', '방울토마토는 꼭지를 제거하고 물기없이 준비해요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '08a40d629a8146f1852c39a9c3c5ac63.jpg', '마늘은 끝부분을 잘라요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '2d506f3ad2f7467eb1f0fd43d058f3eb.jpg', '그릇에 방울토마토, 통마늘, 올리브유, 소금, 후추, 페페론치노를 넣어 섞어요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '73b664b9734d4baea7a3b08833a5284e.jpg', '180도 에어프라이어에 20분간 구워요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'd689888fe6aa44f199112a962081a1b9.jpg', '꺼내서 바질페스토를 넣어 섞어주고 그라나파다노 치즈를 골고루 뿌려요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '811e98593a0e43ddb92bbfb6dade91d3.jpg', '빵과 함께 곁들여요.', 139);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'e4a3d7ee67a04e25b17664c47fe0731d.jpg', '바지락은 물 1L에 소금 1T~2T(약 35g)를 넣어 해감 해요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '2fb603fb10c64ba19e25a37025d88259.jpg', '냄비에 물 1L를 넣고 소금 5g넣은 후 면을 8~9분간 삶아요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '1634be46a76842b69d5a3edb169f9431.jpg', '면을 건져 올리브오일을 코팅해요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '6a1840a6d36c43b8876e60305d644e9d.jpg', '자색양파, 파슬리는 다져서 준비해요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '4ccfef3da9e54b159e9d8e2fb5c311d7.jpg', '예열된 팬에 올리브유를 3T 넣고 다진마늘, 다진 양파, 페페론치노를 넣어 볶아요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'e0cc0a7456d249938a8be8542f028ae5.jpg', '양파와 마늘이 익으면 바지락을 넣어 살짝 볶아 화이트 와인을 넣고 알코올을 날린 후 뚜껑을 닫아 2~3분간 익혀요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '1c805ac8d0ff4c5cbfe9d83ae9ef43cf.jpg', '3분 후 뚜껑을 열고 면과 다진 파슬리를 넣어 볶아요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '4cffe8de89c743bfbfedbbed42487056.jpg', '간을 본 후 간이 부족하면 소금으로 간을 추가 하고 후추를 뿌려 완성해요.', 140);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '9c9d1fe505154833a3d0b02376b3145e.jpg', '전기밥솥에 씻은 백미, 물, 치킨스톡, 버터를 넣고 취사를 해요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '9c5850344fd64d6a8fa6f6ea086d1b7c.jpg', '양파와 파슬리는 잘게 다져 준비해요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'd4fdbb33ac2e45c79f6650b75e72b1fd.jpg', '팬에 버터 10g을 넣고 다진 마늘과 양파를 넣고 볶아요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '866802ff7e3d4988bbb27c6a82619829.jpg', '양파가 투명해 지면 리조또용 밥을 넣고 한번 섞어요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '8d4e865c5ba64e20bb9f4383ebee074b.jpg', '생크림을과 치킨스톡 절반을 넣고 저어가며 졸여요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'fea8272712434fd7a39e7137ca173201.jpg', '접시에 리조또를 담고, 잠봉과 버터를 올려요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '99b23e11cb694a2582dc6fc5472c0b4a.jpg', '다져 놓은 파슬리를 뿌리고 후추를 뿌려 완성해요.', 141);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '98cf4953039c4b5c86743ae4e64b75c6.jpg', '양배추와 당근은 얇게 채 썰어요.', 142);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '98429e710b6e4873807c69c6e7e3825e.jpg', '양배추는 뜨거운 물에 5분 정도 담가요.', 142);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'bcc33ebae42e4dc9b543d9ac01ac20f3.jpg', '팬에 오일과 마늘, 당근을 넣어 볶아요.', 142);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, NULL, '당근 겉면이 노릇하게 익으면 양배추, 참치액, 페퍼론치노, 후추를 넣어 3분간 볶아요.', 142);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'f0c95d4172144e46abfbbab4f6c81f8f.jpg', '반찬으로 먹어도 좋고 빵과 함께 곁들여도 좋아요.', 142);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '77d6909fb035443db663541a74e8cbd5.jpg', '말차파우더, 뜨거운물을 섞어 말차베이스를 만들어요.', 143);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '761baee670c841d39d620e231e33cabe.jpg', '레몬은 즙을 짜서 30ml 준비해요.', 143);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'bb1060f8a78f486f88f90747aca92a86.jpg', '컵에 말차베이스과 얼음, 레몬슬라이스를 넣어요.', 143);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'c7ecdd4b14d14e36a4f3dc37a7c8910c.jpg', '레몬즙을 넣고 사이다를 부어요.', 143);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '8bda4795d4a547eb867af504890e815e.jpg', '로즈마리를 올려 완성해요.', 143);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '885638aa5224465997e61ef113dd8b48.jpg', '대파는 잘게 다지고 크래미는 결대로 찢어요.', 144);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '69bd2204bab249bf8a962e4314a04563.jpg', '세척한 콩나물은 끓는물에 넣어 3분간 데쳐요.', 144);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'd7643b915090467ca2faea83f6bd140f.jpg', '데친 콩나물은 찬물에 식혀 건져요.', 144);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '4533346cdba04434a7a03d880594e59a.jpg', '볼에 콩나물, 크래미, 대파, 분량의 양념재료를 넣어 살살 무쳐요.', 144);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '70b6c7ca9f854a18a303ffa1cc196cfb.jpg', '통깨를 뿌려 마무리해요.', 144);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '6a2c046b382046678ff097d5f7faa58a.jpg', '시금치는 끝부분을 잘라 손질해요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'b5bc59602ebd4422845b6b3c11338537.jpg', '훈제오리는 한입 크기로 썰어요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '43b2cfac7e9846c895398c8946701983.jpg', '파스타면은 끓는 물에 9분간 삶아요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'a2a7e532ef964b2d9605385dd8c679d1.jpg', '팬에 훈제오리를 넣어 노릇하게 볶아요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'c2cea418008c4985a9716f0252b9b418.jpg', '훈제오리 기름에 올리브유, 다진마늘, 페퍼론치노를 넣어 볶아요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '8ae807689f6a43b2a9b1837a9d0be985.jpg', '마늘 향이 올라오면 시금치와 진간장, 설탕을 넣어 중불에서 30초정도 볶아요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '35cba529dd4d46e4afd2bd269d648954.jpg', '시금치 숨이 죽으면 파스타면과 훈제오리, 참치액젓, 면수 2국자를 넣어 강불에서 볶아요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'fc26b0c3443343929ced90c2dc4c885b.jpg', '파마산치즈를 약간 뿌려 완성해요.', 145);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '66941f0ec3ca4e588a4f03acdd2ee589.jpg', '양파는 가늘게 채썰고, 양송이 버섯은 6등분으로, 방울토마토는 반으로 자르고 이탈리안 파슬리는 줄기를 제거하고 잎을 다져서 준비해요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'f65a9a4587cb44fa8bc048194738989f.jpg', '달궈진 팬에 올리브오일을 약간 두르고 약한 불에서 양파를 볶아요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '1ef9b0afc5ef47f1b75a051cfa3187d6.jpg', '양파가 투명해지면 양송이 버섯을 넣고, 소금, 후추, 허브믹스 약간으로 간하며 볶아요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '942b49ef06e9453cb65af01fee51a1a0.jpg', '마지막으로 방울토마토를 넣어 살짝 만 더 볶아요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'f332968709bf41f69a21c646a984bba0.jpg', '계달걀은 원하는 정도로 프라이 해주세요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'ff5fe41ab6b541fc893eb0341c62b74b.jpg', '냄비에 오트밀, 물, 토마토소스, 소금, 후추, 허브믹스 약간을 넣고 잘 섞은 후 약한 불에서 2분 정도 끓여요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'adc7604431294491a7be7d77a44a814c.jpg', '끓인 오트밀을 그릇에 담고 볶은 채소와 브리 치즈를 올리고 후라이한 달걀도 올려요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '8330fcc3a2bd43db973ff657605b1dd9.jpg', '마지막으로 이탈리안 파슬리 약간을 곁들여 완성해요.', 146);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'af2ebf5cbbaa40e28271b877d96f0728.jpg', '[미트부스터 김치 고기 비빔국수] 1. 볼에 분량의 양념 재료를 넣어 양념장을 만들어요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '12a941317aad4ed6aaf1418ddf004e44.jpg', '2. 김치미트부스터는 예열된 팬에 케이싱을 벗겨 내 노릇하게 볶아내요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'cc533bf1ce704d7abc4ce2e0aa2b6725.jpg', '3. 끓는 물에 소면을 넣어 익힌 후 찬물에 헹궈 준비해요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '41ca0a1341cf4d90abcacc6771ef4492.jpg', '4. 그릇에 소면을 담은 후 고기, 양념을 올려요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '72e1bf5fe4cc43c980b555827db3061f.jpg', '[미트부스터 불고기 모닝빵 버거] 1. 오이는 슬라이스로 얇게 자르고 양상추는 모닝빵에 넣기 좋게 자르고 모닝빵은 반 잘라 준비해요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '36587b24334346a58db3394da509baf3.jpg', '2.케찹, 마요네즈, 올리고당을 섞어 소스를 만들어요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '24fb8a45635a43a0b073b26f66295488.jpg', '3. 미트 부스터는 케이싱을 벗겨 예열된 팬에 동그랗게 만들어 노릇하게 구워 내요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '91853798218340af804106bcdcd0fb57.jpg', '3. 빵 양쪽에 소스를 발라요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '0568be39d2c64f00bee456c90a292326.jpg', '4. 빵 위로 양상추, 오이, 고기를 올리고 빵을 올려 모닝빵 버거를 완성해요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'fa07cffc985d4a86bfd5ffbefcdd4f68.jpg', '[미트부스터 이탈리안 미트볼 파스타] 1. 끓는 물에 푸실리 면을 넣어 약 10분간 삶아요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'a3c03afcb6ef42c7b1aebcb59ed65287.jpg', '2. 면을 삶는 동안 팬에 미트부스터 이탈리안을 넣어 노릇하게 구워요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '2c76b08b197c4c069047c2f888b03dbd.jpg', '팬에 올리브유를 약간 두른 후, 푸실리, 토마토 소스, 면수, 구운 미트부스터, 블랙올리브를 넣어 볶아요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '35606fbf43db4bcb8fdeacb06a3aabe8.jpg', '파마산 치즈가루를 넣어 완성해요.', 147);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'de64b39af4fc4da0878c37525ffdb543.jpg', '참외 두개를 듬성듬성 반만 남기고 껍질을 벗겨 준비해요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '500eefd508104644862248a061c6298c.jpg', '참외를 세로로 반을 자른 후 속을 긇어 채에 눌러 즙을 내려요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '8ce4a24f8ecc43febd1363bd19eb1606.jpg', '참외는 얇게 썰어요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'f82e8442a5fa4e089db9e880981ed64e.jpg', '참외즙에 올리브유 3큰술, 사과식초 1큰술, 레몬즙 1/2개, 소금 약간을 넣어 섞어요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '3704ea0bd00b4799a0445be3f10cd897.jpg', '평평한 접시에 참외 잘 놓고 드레싱 뿌려요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'a172ef5100854747a98192a49bc12e80.jpg', '레몬제스트를 갈아 올려 완성해요.', 148);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '02a6cca33ecc4f2dba90460a9b94193c.jpg', '베이컨을 볶아요', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '4d33c8c584ef446c96c756669aea171e.jpg', '방울토마토는 1/4로 자르고 시금치는 듬성듬성 자르고 양파는 다져서 준비해요.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '0a2bb4ae25204560b4fa73243a3d1f3b.jpg', '또띠아 중약불에 앞 뒤로 구워서 준비해요.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '763aefeb43a9455098b920b495aad2d3.jpg', '볼에 그릭요거트 2큰술, 마요네는 1큰술, 올리고당 0.5큰술 넣어 섞어 드레싱을 만들어요.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '252c49ff618642079da8c9668d49961f.jpg', '볼에 시금치, 볶은 베이컨, 다진 양파 넣고 올리브오일 두르고 소금 약간 넣고 버무려 토핑을 만들어요.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '3b228a766e0e46b5a66ca0d64946942b.jpg', '구운또띠아에 드레싱을 바르고 버무려놓은 토핑을 듬뿍 올린다.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '221cf10cd4934a30a6e5d7daef6ba4c3.jpg', '파마산치즈는 듬뿍 갈아서 뿌려 완성해요.', 149);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '51aefe7bef8546139968da564b1d9d81.jpg', '버터는 부드럽게 풀어주고 황설탕, 소금과 넣고 크림형태가 되도록 섞어요.', 150);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'cfeb8b6bbdf946168096696b1fcc830f.jpg', '달걀은 바닐라익스트렉과 함께 섞어 풀어요.', 150);
