INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '232e1eca03d644679de21f2dafd7c49a.jpg', '두부조림 황금 양념장', '제가 가장 좋아하는 반찬중 하나인 두부요리! 바로 두부조림 황금레시피 인데요 마트에서 저렴하게 사올수 있는 재료인 두부와 집에있는 간단한 양념장으로 맛있게 만들수 있는 요리에요 ​참 여러가지 양념장으로 만들어보곤 했는데 저는 요 양념장이 먹어도 먹어도 참 맛있더라구요 또 만들어서 냉장고에 두고 밥먹을때 꺼내먹는데 차가워도 맛있어요!', SYSDATE, '초급', '30분', '매움', '2인분', '' , 24); -- 1
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '387d1b1a64794ebea05d2c2552ddb083.jpg', '매운 제육볶음', '밥과 함께 먹으면 제일 맛있는 제육볶음 황금레시피를 준비해보았어요 상추와 함께 싸먹거나 밥에 비벼먹으면 다른 반찬 필요없이 너무 행복한 메뉴랍니다 만드는법도 정말 너무 간단해서 손님초대요리나 저녁메뉴로 추천드려요 매콤달콤한 맛이 입맛을 돋아서 정말 맛있는 제육볶음이랍니다', SYSDATE, '중급', '30분', '매움', '3인분', '' , 4); -- 2
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'aec023b37b6e466fa3b83401516b2597.jpg', '백선생 시금치무침', '', SYSDATE, '초급', '15분', '아주 안매움', '2인분', '' , 20); -- 3
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '51827c795ce5412cb1173f21a90ca0a9.jpg', '청경채 된장무침', '청경채도 무침으로 먹으면 맛있을 것 같아서 만들었어요~', SYSDATE, '초급', '10분', '아주 안매움', '3인분', '' , 22); -- 4
