INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '4bd4af71ebfa4fbabe2049e20a8ca9c5.jpg', '먼저 두부 1모는 먹기좋게 썰어주고 키친타올로 물기를 닦아줍니다 그릇에 두부를 펴놓고 소금을 살짝 뿌려 밑간을 해주고요', 1);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '75ba2dae612d43f4b5ba7f424f1fcffb.jpg', '​ 양념장을 만들어줍니다 양념은 ​ 진간장 4스푼 / 다진마늘 1스푼 고춧가루 1스푼 / 매실액 1스푼 올리고당 1.5스푼 / 참기름 1스푼 / 물 반컵 으로 만들고요 ​', 1);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '3e162414e9724e8ab367d6faac71b371.jpg', '양념장까지 완성됬으면 이제 본격적으로 요리 시작! 두부를 양념장에 간단하게 조려주기만 하면되는데요 팬에 기름을 두르고 두부를 앞뒤로 노릇하게 구워줍니다', 1);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'ae62ea0d15664122b4c93babea07265c.jpg', '두부가 노릇하게 구워지면 만들어둔 양념장을 부어 맛있게 조려줍니다 중간중간 양념이 두부에 잘 베이도록 양념장을 끼얹어 주기도 하구요 간간히 두부를 뒤집어 주기도 합니다 ​ 어느정도 양념이 조려질때까지 쭉- 반복해주세요', 1);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '37f3e12d90bb4dec8ee65c73c20a787d.jpg', '양념이 두부에 잘 베였다 싶으면 불을 끄고 그릇에 담아 통깨를 뿌려주었어요 ​', 1);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'b18ebc9107e24a8eb23f2d70b32ff64c.jpg', '재료를 준비해주세요', 2);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'b49100973c674c348b1888d09f76d558.jpg', '양념재료를 모두 넣어주세요', 2);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '77443b3b1b79492f844b7abecb74fca3.jpg', '양념재료가 잘 섞이도록 해주세요', 2);
