INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '76934310c10a4e1381cf1f4e18a9d62b.jpg', '양념 후 바로 볶아도 되지만 냉장고에 30분정도 두어 숙성시켜주면 양념이 베어 더욱 맛있답니다', 2);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, 'b8559d3a0daf44869f89fc21e102c2d5.jpg', '팬에 식용유 2큰술과 대파를 넣고 강불로 3분정도 볶아 파기름을 내주세요', 2);
INSERT INTO recipe_step VALUES (seq_recipe_step_id.NEXTVAL, '84ad267b238e4fb1bf94e1e0cdc6a79c.jpg', '양념한 고기를 넣어주세요', 2);
