INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '8f76b0b2720b7c9ab0edd31b8f000fb7.jpg', '미나리레몬파스타', '미나리가 들어간 미나리레몬파스타 어떠세요? 이름만 들어도 상큼함이 물씬 느껴지시죠! 미나리의 은은한 향이 물씬 나면서 레몬이 들어가 입맛을 돋우는 깔끔한 맛! 오늘 저녁으로 드셔보세요~~', SYSDATE, '중급', '20분', '안매움', '1인분', '' , 13); -- 1
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '79215136F1F34F82BD857952880E5DC3.jpg', '건두부무침', '건두부의 고소함은 물론이고 새콤,달콤,매콤함의 소스가 정말 잘 어울려요. 오이로 상큼함과 비타민까지 챙기고 두부로 고소함과 단백질까지 맛과 영양, 다이어트까지 할 수 있는데 조리법도 간단해요! 꼭 따라해보세요.^^', SYSDATE, '초급', '20분', '아주 안매움', '1인분', '' , 14); -- 2
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'EF7DBE36F6AC46D9A949D8C46AA96E54.jpg', '짜파게티 간짜장', '짜장은 소스를 솥에 끓여 미리 만들어두는데요. 간짜장은 주문이 들어오면 즉시 기름으로 춘장, 야채, 돼지고기를 볶아 만들어요! 오늘은 쉽고 빠르게 짜파게티로  간짜장 만드는 방법을 공개할게요!', SYSDATE, '초급', '20분', '안매움', '2인분', '' , 7); -- 3
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'AF7C21452C4D44C6ABFA32911885341D.jpg', '꽈리고추소고기볶음', '장조림인 것 같지만 조금 다른 꽈리고추 소고기볶음 레시피에요. 간장, 굴소스로 간을 해 짭쪼름하면서 꽈리고추가 들어가 매~콤한 맛까지! 요거~ 요거~ 완전 밥도둑 반찬이니 오늘 저녁에 꼬옥 해드셔 보세요~', SYSDATE, '초급', '30분', '보통', '2인분', '' , 7); -- 4
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'AF7C21452C4D44C6ABFA32911885341D.jpg', '토마토스프', '미리 만들어 놓으면 아침에 속 편하게 먹을 수 있는 스프. 건강하고 감칠맛 있는 토마토 스프는 먹을 수록 맛이 있더라고요. 첨에는 좀 밍밍한데 하다가 먹으면서 더 매력을 느낄수 있는 스프예요. 만들어 놓고 소분해서 냉동 보관해 하나씩 데워 먹으면 좋겠어요.', SYSDATE, '초급', '30분', '보통', '2인분', '' , 5); -- 5
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'D08FACDA286C47108FBA4FB961B90C9C.jpg', '한판탕수육', 'SNS보다 이거다 싶어 제 스타일로 만들기 시작! 재료준비는 간단하고 집에 있는 채소로 냉장고 파먹기도 가능해요. 간단히 적은 기름으로 만들수 있다는게 장점이에요. 한판으로 2인이 맛있게 먹을 수 있었어요. 전 부먹파라 부어서 먹었지만 찍먹파들은 따로 덜어 드세요.!! 저 믿고 한번 만들어보세요.~', SYSDATE, '중급', '30분', '안매움', '2인분', '' , 11); -- 6
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'BB8635CA07DB4BC68A04581A0BFB8467.jpg', '어묵우동', '지인이 사누끼 냉동면을 추천해 주어서 구매 후 냉동보관 해둔 우동! 날씨가 쌀쌀해지니 뜨끈한 국물이 생각나서 초간단 우동을 만들었어요. 어묵과 대파, 양념만 있으면 힘들게 육수 낼 필요 없이 간단하게 만들수 있어요. 개인적으로 어묵이 없이 만들어도 깔끔한 우동 국물을 즐길 수 있더라고요. 거창한 재료 없이도 깔끔하게 육수도 만들고 사누끼 우동 면의 쫄깃한 면발까지 아주 좋았어요. 우동 러버들은 한번 만들어보면 좋을 것 같아요.', SYSDATE, '초급', '15분', '안매움', '1인분', '' , 6); -- 7
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'AC33F19477CE4C4188DFFCA0A3AC0B2E.jpg', '칠리오일물만두', '입이 심심할 때 어른 간식으로 완전 제격인 칠리오일물만두 레시피 알려드릴게요! 매콤달콤한 칠리오일소스를 물만두 위에 휘리릭 부어주면 완성되는 초간단 간식이에요.', SYSDATE, '중급', '15분', '보통', '2인분', '' , 10); -- 8
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '42111DF615C0460AB9926951762F4467.jpg', '알배추명란덮밥', '명란 좋아하는 사람 여기 붙어라~알배추를 곁들인 명란 덮밥이 왔어요! 알배추, 명란, 달걀만 있으면 뚝딱! 15분만에 쉽고 맛있는 덮밥 만들어 보세요!', SYSDATE, '초급', '15분', '안매움', '1인분', '' , 6); -- 9
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '4253811251CF49FCA71DD3D3BAEC44E1.jpg', '가지돼지고기덮밥', '돼지고기의 식감이 살아있고 가지와 양파에 양념이 잘 베어 너무 잘 어울리는 맛이에요. 야채 싫어하는 아이들 먹기도 좋고 어른들도 영양도 맛도 챙길 수 있는 가지돼지고기 덮밥 만들어 보세요!', SYSDATE, '초급', '20분', '안매움', '1인분', '' , 12); -- 10
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'FF21526A377F4C7B87E526E039645AEF.jpg', '봄동새우전', '날 풀리고 이제 봄이 오나봐요! 봄을 알리는 제철 재료 ‘봄동’을 활용한 고소하고 맛있는 봄동새우전 봄동무침, 봄동나물도 좋기만 전으로 부쳐먹으면 소리부터 식감까지 오감만족 반찬이에요.', SYSDATE, '초급', '20분', '아주 안매움', '2인분', '' , 6); -- 11
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '239707A443764C72980FBFB95742076A.jpg', '또띠아삼각피자', '또띠아를 고이 접어 만드는 디저트 집에서도 호다닥 만들 수 있는 5분컷 또띠아 삼각피자 또띠아 안에 토마토소스, 베이컨, 콘옥수수, 치즈를 넣어 접어주면 삼각피자 준비 완료! 팬에서 노릇노릇하게 구워주면 치즈가 주~욱 늘어나는 간식이 완성~', SYSDATE, '초급', '15분', '안매움', '2인분', '' , 10); -- 12
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'CDEABE5AB8144D3C8E424D7725EF3D2A.jpg', '명란두부찌개', '한 숟가락 먹는 순간 맛있어서 놀라는 찌개였어요. 조리법은 정말 간단한데 깊은 맛의 한식을 먹는 기분 육개장 같기도 두부찌개 같기도 하면서 고급스러운 찌개에요. 손님접대 어른접대는 물론 온 가족이 든든하게 먹기 좋아요.', SYSDATE, '초급', '30분', '안매움', '2인분', '' , 9); -- 13
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '63DDCFD25A9C43C1A171483171639896.jpg', '청경채버섯덮밥', '간단하게 만드는 한그릇 요리 술술 넘어가는 청경채버섯덮밥! 식감이 좋은 양송이버섯과 만가닥버섯 그리고 청경채와 새우를 넣어 맛이 없을 수 가없죠~점심으로도 저녁으로도 좋은 메뉴 맛나게 드시고 모두 월요팅하세요!', SYSDATE, '초급', '20분', '안매움', '2인분', '' , 13); -- 14
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '80B3C40D76E34421994410B8F7FAB627.jpg', '냉이고추장무침', '따뜻해진 날씨, 싱그러운 봄이 좋은 이유는? 향긋한 나물들이 가득하기 때문이지요! 대표적인 봄나물 냉이로 만드는 밑반찬 고추장 소스로 만들어 입맛이 돌아와요. 김이 모락모락 나는~고슬고슬 흰밥 위에 냉이고추장무침 올려서 맛있는 한끼 드세요~', SYSDATE, '초급', '10분', '매움', '2인분', '' , 12); -- 15
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '7F0EBB59184D4B309E26A3534340D698.jpg', '케일김치', '케일 특유의 쌉쌀한 맛 때문에 안 드시는 분들도 꽤나 많을 텐데요.  케일에는 엄청난 항산화 성분이 들어있고 면역력 강화, 혈액 순환에 도움을 줘요!  미세먼지, 황사가 심한 요즘 같은 날 케일김치 만들어 먹고 내건강 지켜보세요.', SYSDATE, '중급', '20분', '매움', '4인분', '' , 6); -- 16
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '974F292DD4574B4FBDB413B57B0841CC.jpg', '명란볶음밥', '먹자마자 라꽁비에뜨 버터향이 입안에 퍼지며 고급진 맛이 났어요. 리조토같은 느낌까지 들었어요. 달콤한 야채와 짭조름한 명란이 꿀조합인데 식감까지 느껴져서 먹는 내내 기분 좋은 맛이었어요.', SYSDATE, '초급', '30분', '안매움', '2인분', '' , 8); -- 17
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, '22704AB4AC804BD88151526BD62C67A9.jpeg', '두부밥포케', '칼로리는 낮추고 포만감은 꽉 채운 한 끼 담백한 두부에 고소한 참치, 새콤한 포케 드레싱까지! 가볍지만 맛있게 즐겨봐요~', SYSDATE, '초급', '15분', '안매움', '1인분', '' , 12); -- 18
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'CFFA2E210A244597ADE09D6C2D63CEB9.jpeg', '컵오므라이스', '요즘 sns에서 자주 보이는 컵오므라이스! 베이컨+치즈+달걀의 환상 조합 간단하지만 맛은 꽉 찬 한 끼! 컵 하나로 만드는 초간단 레시피, 지금 바로 도전해보세요', SYSDATE, '초급', '5분', '안매움', '1인분', '' , 7); -- 19
INSERT INTO recipe VALUES (seq_recipe_id.NEXTVAL, 'A71B96F8DE2549CF8AC1720288571EC2.jpg', '바나나 또띠아 토스트', '주말 아침으로 완벽한 토스트 레시피! 아이도 엄빠도 함께 먹기 좋은 토스트  바로 바나나 또띠아 토스트에요. 땅콩버터가 들어가 고소함이 가득~ 아몬드와 피칸까지 넣어 식감도 UP 피자치즈 넣어 쭈~욱 늘려 먹는 재미까지.  주말 아침으로 또는 간식으로 드셔보세요', SYSDATE, '초급', '15분', '안매움', '1인분', '' , 6); -- 20
